from EXF.SZA_STOCK
WHERE WERKS = #{plant}
AND FAB_DATE= TO_DATE(#{fabDate},'YYYY-MM-DD')
AND MATNR=#{material}
AND LGORT IN

  #{item}

select MATNR as material, SUM(LABST) as qty
from EXF.SZA_STOCK
WHERE WERKS = #{plant}
AND FAB_DATE= TO_DATE(#{fabDate},'YYYY-MM-DD')
AND MATNR IN

  #{item}

AND LGORT IN

  #{item}

